mapper.select:
select post.ID,post.USER_ID,post.KIND_NAME,post.TITLE,post.CONTENT,post.CREATED_TIME,post.LIKE,post.STAR,user.NICKNAME,user.HEAD_URL FROM post,user WHERE post.USER_ID=user.ID
and KIND_NAME=#{kindName}
and post.ID <=(select post.ID from post order by post.ID desc limit #{offset},1) order by post.ID desc limit #{pageSize}
