mapper.select:
select FILENAME, URL, ISUPLOAD
from video
where USER_ID = #{userId}

mapper.update:
update video
set ISUPLOAD = 1
where USER_ID = #{userId}
  and URL = #{videoUrl}
  and ISUPLOAD = 0

mapper.delete:
delete
from video
where USER_ID = #{userId}
  and URL = #{videoUrl}
  and ISUPLOAD = 1
